import React, { useEffect, useState } from 'react'
import movieApi from '../api/movieApi'

const ListMovies = () => {

const [movies, setMovies] = useState([])

// get list of movies from API and pass to movies STATE
useEffect(() => { 
movieApi.getAllMovies().then((response) =>{
    setMovies(response.data)
    console.log(response.data)
}).catch(error => {
    console.log(error)
})
}, [])


  return (
    <div className='container'>
        <h2 className='text-center'>Put It On The List</h2>
        <Link to ="/addMovie" className = "btn btn-primary mb-2">Add Movie</Link>
        <table className='table table-bordered table-striped'>
            <thead>
                <th>Movie Name</th>
                <th>Movie Director</th>
                <th>Movie Actor</th>  
            </thead>
            <tbody>
                {
                    movies.map(
                        movies =>
                        <tr key= {movies.id}>
                             <td>{movies.movieName}</td>
                             <td>{movies.director}</td>
                             <td>{movies.actor}</td>
                        </tr>
                       
                    )
                }
            </tbody>
        </table>

    </div>
  )
}

export default ListMovies
